import { Component } from 'react';

export default class CharactersCard extends Component{
    constructor(props){
        super(props)
        this.state = {
            favsMessage: 'Añadir'
        }
    }



    render(){
        return(
            <div>
                <img src={this.props.data.image}/>
                <h4>{this.props.data.name}</h4>
                <p>{this.props.data.species}</p>
                <button onClick={() => this.agregarYQuitarDeFavoritos(this.props.data.id)}>Añadir a favoritos</button>
            </div>
        )
    }
}
