import { Component } from 'react';

export default class CharactersCard extends Component{
    constructor(props){
        super(props)
        this.state = {
            button: 'Añadir'
        }
    }

    componentDidMount(){
        let favoritos = [];
        let storage = localStorage.getItem('favoritos');
        if(storage !== null){
            let favoritosToArray = JSON.parse(storage);
            favoritos = favoritosToArray
        }

        if(favoritos.includes(this.props.data.id)){
            this.setState({
                favsMessage: 'Quitar de favoritos'
            })
        }
    }

    addQuitFav(id){
        let favoritos = []
        let storage = localStorage.getItem('favoritos')
        if(storage !== null){
            let parse = JSON.parse(storage)
            favoritos = parse
        }
        if (favoritos.includes(id)){
            favoritos = favoritos.filter(idx => idx !== id)
            this.setState({
                button: 'Añadir'
            })
        } else {
            let newFav = favoritos.push(id);
            localStorage.setItem('favoritos', newFav);
            this.setState({
                button: 'Quitar'
            })
        }
    }

    render(){
        return(
            <div>
                <img src={this.props.data.image}/>
                <h4>{this.props.data.name}</h4>
                <p>{this.props.data.species}</p>
                <button onClick={() => this.addQuitFav(this.props.data.id)}>Añadir a favoritos</button>
            </div>
        )
    }
}
