import CharactersCard from '../CharactersCard/CharactersCard';
import { Component } from 'react';

export default class Characters extends Component{
    constructor(props){
        super(props)
        this.state = {
            data: [],
            page: 1
        }
    }

    componentDidMount(){
        fetch('https://rickandmortyapi.com/api/character/?page=' + this.state.page)
        .then(res => res.json())
        .then(data => this.setState({ data: data.results }))
        .catch(err => console.log(err))
    }

    moreCards(){

        fetch('https://rickandmortyapi.com/api/character/?page=' + this.state.page)
        .then(res => res.json())
        .then(data => this.setState({ page: this.state.page, data}))

        this.setState({
            page: this.state.page + 1
        })
    }

    render(){
        return(
            <div>
            {this.state.data.map((card, idx) => <CharactersCard key={card + idx} data={card}/>)}
            <button onClick={() => this.moreCards()}>MÃ¡s Cartas</button>
            </div>
        )
    }
}
