import { Component } from 'react';

export default class CharactersCard extends Component{
    constructor(props){
        super(props)
        this.state = {
            button: true
        }
    }

    addQuitFav(id){
        let favoritos = [];
        let storage = localStorage.getItem('favoritos');
        if(storage !== null){
            let favoritosArray = JSON.parse(storage);
            favoritos = favoritosArray
        }
        if(!favoritos.includes(id)){
            favoritos.push(id)
        } else {
            favoritos = favoritos.filter(iD => iD !== id)
        }
    }

    a単adirSacar(id) {
        let storage = JSON.parse(localStorage.getItem('favoritos'))

        if (storage.includes(id)) {
            storage = storage.filter(ID => ID !== id)
            this.setState({
                favorito: false
            })
        } else {
            storage.push(id)
            this.setState({
                favorito: true
            })
        }

        localStorage.setItem('favoritos', JSON.stringify(storage))
      }

    render(){
        return(
            <div>
                <img src={this.props.data.image}/>
                <h4>{this.props.data.name}</h4>
                <p>{this.props.data.species}</p>
                {this.state.button ? 
                <button onClick={() => this.addQuitFav(this.props.data.id)}>A単adir</button>
                :
                <button onClick={() => this.addQuitFav(this.props.data.id)}>Quitar</button>
                }
            </div>
        )
    }
}
