import { Component } from 'react';

export default class CharactersCard extends Component{
    constructor(props){
        super(props)
        this.state = {
            characters: this.props.data
        }
    }
    render(){
        return(
            <div>
                <img src={this.props.data.img}/>
                <h4>{this.state.characters.name}</h4>
                <p>{this.state.characters.description}</p>
            </div>
        )
    }
}
