import { Component } from "react";
import { Link } from "react-router-dom";

export default class FavoritosCard extends Component{
    constructor(props){
        super(props)
        this.state = {

        }
    }

    componentDidMount(){
        fetch('https://rickandmortyapi.com/api/character/' + this.props.match.params.id)
        .then(res => res.json())
    }

    render(){
        return(
            <main>
                <img src={this.props.data.image}/>
                <h1>{this.props.data.name}</h1>
                <p>{this.props.data.gender}</p>
                <button onClick={() => this.props.borrar(this.props.data.id)}>Quitar</button>
            </main>
        )
    }
}
