import CharactersCard from '..CharactersCard/CharactersCard/CharactersCard';
import { Component } from 'react';

export default class Characters extends Component{
    constructor(props){
        super(props)
        this.state = {
            info: this.props.data
        }
    }
    render(){
        return(
            <div>
            {this.state.info.map((card, idx) => <CharactersCard key={card + idx} data={card}/>)}
            </div>
        )
    }
}
