import { Component } from "react";
import { Link } from "react-router-dom";

export default class FavoritosCard extends Component{
    constructor(props){
        super(props)
        this.state = {
            data: []
        }
    }

    componentDidMount(){
        fetch('https://rickandmortyapi.com/api/character/' + this.props.match.params.id)
        .then(res => res.json())
        .then(data => this.setState({ data: data }))
        .catch(err => console.log(err))
    }

    render(){
        return(
            <main>
                <img src={this.state.data.image}/>
                <h1>{this.props.data.name}</h1>
                <p>{this.props.data.gender}</p>
                <button onClick={() => this.props.borrar(this.props.data.id)}>Quitar</button>
            </main>
        )
    }
}
