import { Component } from 'react';

export default class CharactersCard extends Component{
    constructor(props){
        super(props)
        this.state = {
            favoritos: []
        }
    }

    addFavorito(id){
        let newFav = this.state.favoritos.concat(id)

        localStorage.setItem('favoritos', newFav);
        this.setState({ favoritos: newFav})
    }

    render(){
        return(
            <div>
                <img src={this.props.data.image}/>
                <h4>{this.props.data.name}</h4>
                <p>{this.props.data.species}</p>
                <button onClick={() => addFavorito(this.props.data.id)}>AÃ±adir a favoritos</button>
            </div>
        )
    }
}
